import axios from 'axios';
import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

const AddEmployee = () => {
    const [employee, setEmployee] = useState({
        first_name: '',
        last_name: '',
        email: '',
        salary: '',
        date: '',
        is_permanent: false,
    });

    const history = useHistory();

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setEmployee({ ...employee, [name]: type === 'checkbox' ? checked : value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        await axios.post('http://localhost:5000/api/employees', employee);
        history.push('/');
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Add Employee</h2>
            <input name="first_name" placeholder="First Name" onChange={handleChange} required />
            <input name="last_name" placeholder="Last Name" onChange={handleChange} required />
            <input name="email" placeholder="Email" onChange={handleChange} required />
            <input name="salary" placeholder="Salary" onChange={handleChange} required />
            <input name="date" type="date" onChange={handleChange} required />
            <label>
                Is Permanent:
                <input name="is_permanent" type="checkbox" onChange={handleChange} />
            </label>
            <button type="submit">Add</button>
        </form>
    );
};

export default AddEmployee;
