import axios from 'axios';
import React, { useEffect, useState } from 'react';
import '../App.css'; // Ensure this path is correct

const EmployeeList = () => {
    const [employees, setEmployees] = useState([]);
    const [editingEmployee, setEditingEmployee] = useState(null);
    const [showModal, setShowModal] = useState(false);

    useEffect(() => {
        const fetchEmployees = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/employees');
                setEmployees(response.data);
            } catch (error) {
                console.error('Error fetching employees:', error);
            }
        };
        fetchEmployees();
    }, []);

    const handleEdit = (employee) => {
        setEditingEmployee(employee);
        setShowModal(true);
    };

    const handleDelete = async (id) => {
        try {
            const response = await axios.delete(`http://localhost:8080/api/employees/${id}`);
            if (response.status === 200) {
                setEmployees(employees.filter(emp => emp.id !== id));
                alert('Employee deleted successfully!');
            }
        } catch (error) {
            console.error('Error deleting employee:', error);
        }
    };

    return (
        <div className="table-container">
            <h1>Employee Management Software</h1>
            <div className="left-align">
                <a href="/add-employee" className="add-btn">Add Employee</a>
            </div>
            <table>
                <thead>
                    <tr>
                        <th>No.</th>
                        <th>First Name</th>
                        <th>Last Name</th>
                        <th>Email</th>
                        <th>Salary</th>
                        <th>Date</th>
                        <th>Permanent?</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {employees.map((employee, index) => {
                        // Format the date to YYYY-MM-DD
                        const formattedDate = new Date(employee.date).toISOString().split('T')[0];

                        return (
                            <tr key={employee.id}>
                                <td>{index + 1}</td>
                                <td>{employee.firstName}</td>
                                <td>{employee.lastName}</td>
                                <td>{employee.email}</td>
                                <td>${employee.salary}</td>
                                <td>{formattedDate}</td>
                                <td>{employee.isPermanent ? 'Yes' : 'No'}</td>
                                <td>
                                    <button className="edit-btn" onClick={() => handleEdit(employee)}>Edit</button>
                                    <button className="delete-btn" onClick={() => handleDelete(employee.id)}>Delete</button>
                                </td>
                            </tr>
                        );
                    })}
                </tbody>
            </table>
            {showModal && (
                <div className="modal">
                    {/* Modal content for editing employee */}
                </div>
            )}
        </div>
    );
};

export default EmployeeList;
