// client/src/components/AddEmployee.js

import axios from 'axios';
import React, { useState } from 'react';

const AddEmployee = () => {
    const [employee, setEmployee] = useState({
        firstName: '',
        lastName: '',
        email: '',
        salary: '',
        date: '',
        isPermanent: false
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEmployee({
            ...employee,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        axios.post('/api/employees', employee)
            .then((response) => {
                alert('Employee added successfully');
                setEmployee({
                    firstName: '',
                    lastName: '',
                    email: '',
                    salary: '',
                    date: '',
                    isPermanent: false
                });
            })
            .catch((error) => {
                console.error('There was an error adding the employee!', error);
            });
    };

    return (
        <div style={{ textAlign: 'center', marginTop: '20px' }}>
            <h2>Add Employee</h2>
            <form onSubmit={handleSubmit}>
                <div style={{ margin: '10px 0' }}>
                    <label>First Name: </label>
                    <input type="text" name="firstName" value={employee.firstName} onChange={handleChange} required />
                </div>
                <div style={{ margin: '10px 0' }}>
                    <label>Last Name: </label>
                    <input type="text" name="lastName" value={employee.lastName} onChange={handleChange} required />
                </div>
                <div style={{ margin: '10px 0' }}>
                    <label>Email: </label>
                    <input type="email" name="email" value={employee.email} onChange={handleChange} required />
                </div>
                <div style={{ margin: '10px 0' }}>
                    <label>Salary ($): </label>
                    <input type="number" name="salary" value={employee.salary} onChange={handleChange} required />
                </div>
                <div style={{ margin: '10px 0' }}>
                    <label>Date (dd/mm/yyyy): </label>
                    <input type="date" name="date" value={employee.date} onChange={handleChange} required />
                </div>
                <div style={{ margin: '10px 0' }}>
                    <label>Permanent: </label>
                    <input type="checkbox" name="isPermanent" checked={employee.isPermanent} onChange={() => setEmployee({ ...employee, isPermanent: !employee.isPermanent })} />
                </div>
                <div style={{ margin: '20px 0' }}>
                    <button type="submit">Add Employee</button>
                </div>
            </form>
        </div>
    );
};

export default AddEmployee;
