import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AddEmployee = () => {
    const [employee, setEmployee] = useState({
        firstName: '',
        lastName: '',
        email: '',
        salary: '',
        date: '',
        isPermanent: false,
    });
    const navigate = useNavigate();

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setEmployee({ ...employee, [name]: value });
    };

    const handleCheckboxChange = (e) => {
        setEmployee({ ...employee, isPermanent: e.target.checked });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:5000/api/employees', employee);
            navigate('/');
        } catch (error) {
            console.error('Error adding employee:', error);
        }
    };

    return (
        <div className="form-container">
            <h2>Add Employee</h2>
            <form onSubmit={handleSubmit}>
                <label>First Name:</label>
                <input type="text" name="firstName" value={employee.firstName} onChange={handleInputChange} required />

                <label>Last Name:</label>
                <input type="text" name="lastName" value={employee.lastName} onChange={handleInputChange} required />

                <label>Email:</label>
                <input type="email" name="email" value={employee.email} onChange={handleInputChange} required />

                <label>Salary ($):</label>
                <input type="number" name="salary" value={employee.salary} onChange={handleInputChange} required />

                <label>Date:</label>
                <input type="date" name="date" value={employee.date} onChange={handleInputChange} required />

                <label>
                    <input type="checkbox" name="isPermanent" checked={employee.isPermanent} onChange={handleCheckboxChange} />
                    Is Permanent
                </label>

                <button type="submit">Add</button>
            </form>
        </div>
    );
};

export default AddEmployee;
